import { useState } from 'react'

const Button = (props) => {
  return (
    <button onClick={props.increase}>
      {props.text}
    </button>
  )
}

const StatisticLine = (props) => (
  <div>
    {props.name} {props.value}
  </div>
)

const Statistics = (props) => {
  const good = props.good
  const neutral = props.neutral
  const bad = props.bad
  const all = good + neutral + bad;
  const score = (good * 1) + (neutral * 0) + (bad * (-1))
  const percentage = (good / all) * 100

  if (all > 0) {
    return (
      <div>
        <table>
          <tbody>
            <tr>
              Good
            </tr>
          </tbody>
        </table>
        <StatisticLine name={'good'} value={good} />
        <StatisticLine name={'neutral'} value={neutral} />
        <StatisticLine name={'bad'} value={bad} />
        <StatisticLine name={'all'} value={all} />
        <StatisticLine name={'average'} value={(score / all)} />
        <StatisticLine name={'percentage'} value={percentage} />
      </div>
    )
  } else if (all === 0) {
    return (
      <div>
        No feedback given
      </div>
    )
  }

}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  return (
    <div>
      <h1>give feedback</h1>
      <Button text={'good'} increase={() => setGood(good + 1)} />
      <Button text={'neutral'} increase={() => setNeutral(neutral + 1)} />
      <Button text={'bad'} increase={() => setBad(bad + 1)} />

      <h1>statistics</h1> 
      <Statistics good={good} neutral={neutral} bad={bad} />

    </div>
  )
}

export default App
